package main

import (
	"context"
	"fmt"
	"github.com/weaviate/weaviate-go-client/v4/weaviate"
)

func main() {
    cfg := weaviate.Config{
        Host:   "WEAVIATE_INSTANCE_URL",  // Replace with your Weaviate endpoint
        Scheme: "https",
    }

    client, err := weaviate.NewClient(cfg)
    if err != nil {
      panic(err)
    }
}
